import React, { Component } from "react";

/* Import Components */
// import CheckBox from "../components/CheckBox";
import Text from "./components/text";
// import TextArea from "../components/TextArea";
// import Select from "../components/Select";
import Button from "./components/submit";

class Container extends Component {
  constructor(props) {
    super(props);

    this.state = {
      newUser: {
        firstname: "",
        lastname: "",
        npinum: "",
        address: {
            address1: "",
            address2: "",
            city: "",
            state: "",
            zip: "",
          },
        phone: "",
        email: ""
      },
    };

    this.handleFirstName = this.handleFirstName.bind(this);
    this.handleLastName = this.handleLastName.bind(this);
    this.handleNPInum = this.handleNPInum.bind(this);
    this.handleAddress1 = this.handleAddress1.bind(this);
    this.handlePhone = this.handlePhone.bind(this);
    this.handleEmail = this.handleEmail.bind(this);

    this.handleFormSubmit = this.handleFormSubmit.bind(this);
    this.handleClearForm = this.handleClearForm.bind(this);
  }

  /* This lifecycle hook gets executed when the component mounts */

  handleFirstName(e) {
    let value = e.target.value;
    this.setState(
      prevState => ({
        newUser: {
          ...prevState.newUser,
          firstname: value
        }
      }),
      //() => console.log(this.state.newUser)
    );
  }

  handleLastName(e) {
    let value = e.target.value;
    this.setState(
      prevState => ({
        newUser: {
          ...prevState.newUser,
          lastname: value
        }
      }),
      //() => console.log(this.state.newUser)
    );
  }

  handleNPInum(e) {
    let value = e.target.value;
    let name = e.target.name;
    this.setState(
      prevState => ({
        newUser: {
          ...prevState.newUser,
          npinum: value
        }
      }),
      //() => console.log(this.state.newUser)
    );
  }

  handleAddress1(e) {
    let value = e.target.value;
    this.setState(
      prevState => ({
        newUser: {
          ...prevState.newUser.address,
          address1: value
        }
      }),
      //() => console.log(this.state.newUser)
    );
  }

  handleEmail(e) {
    let value = e.target.value;
    this.setState(
      prevState => ({
        newUser: {
          ...prevState.newUser,
          email: value
        }
      }),
      //() => console.log(this.state.newUser)
    );
  }

  handlePhone(e) {
    let value = e.target.value;
    this.setState(
      prevState => ({
        newUser: {
          ...prevState.newUser,
          phone: value
        }
      }),
      //() => console.log(this.state.newUser)
    );
  }

  handleFormSubmit(e) {
    e.preventDefault();
    let userData = this.state.newUser;

    console.log(userData);

    // fetch("http://example.com", {
    //   method: "POST",
    //   body: JSON.stringify(userData),
    //   headers: {
    //     Accept: "application/json",
    //     "Content-Type": "application/json"
    //   }
    // }).then(response => {
    //   response.json().then(data => {
    //     console.log("Successful" + data);
    //   });
    // });
  }

  handleClearForm(e) {
    e.preventDefault();
    this.setState({
      newUser: {
        firstname: "",
        lastname: "",
        npinum: "",
        address: {
            address1: "",
            address2: "",
            city: "",
            state: "",
            zip: "",
          },
        phone: "",
        email: ""
      }
    });
  }

  render() {
    return (
      <form className="container-fluid" onSubmit={this.handleFormSubmit}>
        {/* First Name */}
        <Text
          inputType={"text"}
          title={"First Name:"}
          name={"firstname"}
          value={this.state.newUser.firstname}
          placeholder={"Enter your first name"}
          handleChange={this.handleFirstName}
        />{" "}
        {/* Last Name */}
        <Text
          inputType={"text"}
          title={"Last Name:"}
          name={"lastname"}
          value={this.state.newUser.lastname}
          placeholder={"Enter your age"}
          handleChange={this.handleLastName}
        />{" "}
        {/* NPI Number */}
        <Text
          title={"NPI Number:"}
          name={"npinum"}
          options={this.state.npinum}
          value={this.state.newUser.npinum}
          placeholder={"Enter your NIP Number"}
          handleChange={this.handleNPInum}
        />{" "}
        {/* Address 1*/}
        <Text
          title={"Address 1:"}
          name={"address1"}
          options={this.state.address1}
          selectedOptions={this.state.newUser.address.address1}
          handleChange={this.handleAddress1}
        />{" "}
        {/* Phone Number */}
        <Text
          title={"Phone Number:"}
          name={"phone"}
          value={this.state.newUser.phone}
          handleChange={this.handlePhone}
          placeholder={"Enter your phone number"}
        />
        {/* Phone Number */}
        <Text
          title={"Email Address:"}
          name={"email"}
          value={this.state.newUser.email}
          handleChange={this.handleEmail}
          placeholder={"Enter your email"}
        />
        {/* Submit */}
        <Button
          action={this.handleFormSubmit}
          type={"primary"}
          title={"Submit"}
          style={buttonStyle}
        />{" "}
        {/*Clear */}
        <Button
          action={this.handleClearForm}
          type={"secondary"}
          title={"Clear"}
          style={buttonStyle}
        />{" "}
        {/* Clear the form */}
        <hr/>
      </form>
    );
  }
}

const buttonStyle = {
  margin: "10px 10px 10px 10px"
};

export default Container;
