import React, { Component } from "react";

/* Import Components */
import Text from "./components/text";
import Button from "./components/submit";

class Container extends Component {
  constructor(props) {
    super(props);

    this.state = {
      newUser: {
        firstname: "",
        lastname: "",
        npinum: "",
        address1: "",
        address2: "",
        city: "",
        state: "",
        zip: "",
        // address: {
        //     address1: "",
        //     address2: "",
        //     city: "",
        //     state: "",
        //     zip: "",
        //   },
        phone: "",
        email: ""
      },
      registrations: []
    };

    this.handleFirstName = this.handleFirstName.bind(this);
    this.handleLastName = this.handleLastName.bind(this);
    this.handleNPInum = this.handleNPInum.bind(this);
    this.handleAddress1 = this.handleAddress1.bind(this);
    this.handleAddress2 = this.handleAddress2.bind(this);
    this.handleCity = this.handleCity.bind(this);
    this.handleState = this.handleState.bind(this);
    this.handleZip = this.handleZip.bind(this);
    this.handlePhone = this.handlePhone.bind(this);
    this.handleEmail = this.handleEmail.bind(this);

    this.handleFormSubmit = this.handleFormSubmit.bind(this);
    this.handleClearForm = this.handleClearForm.bind(this);
  }

  /* This lifecycle hook gets executed when the component mounts */

  handleFirstName(e) {
    let value = e.target.value;
    this.setState(
      prevState => ({
        newUser: {
          ...prevState.newUser,
          firstname: value
        }
      }),
      //() => console.log(this.state.newUser)
    );
  }

  handleLastName(e) {
    let value = e.target.value;
    this.setState(
      prevState => ({
        newUser: {
          ...prevState.newUser,
          lastname: value
        }
      }),
      //() => console.log(this.state.newUser)
    );
  }

  handleNPInum(e) {
    let value = e.target.value;
    let name = e.target.name;
    this.setState(
      prevState => ({
        newUser: {
          ...prevState.newUser,
          npinum: value
        }
      }),
      //() => console.log(this.state.newUser)
    );
  }

  handleAddress1(e) {
    let value = e.target.value;
    this.setState(
      prevState => ({
        newUser: {
          ...prevState.newUser,
          address1: value
        }
      }),
      //() => console.log(this.state.newUser)
    );
  }

  handleAddress2(e) {
    let value = e.target.value;
    this.setState(
      prevState => ({
        newUser: {
          ...prevState.newUser,
          address2: value
        }
      }),
      //() => console.log(this.state.newUser)
    );
  }

  handleCity(e) {
    let value = e.target.value;
    this.setState(
      prevState => ({
        newUser: {
          ...prevState.newUser,
          city: value
        }
      }),
      //() => console.log(this.state.newUser)
    );
  }

  handleState(e) {
    let value = e.target.value;
    this.setState(
      prevState => ({
        newUser: {
          ...prevState.newUser,
          state: value
        }
      }),
      //() => console.log(this.state.newUser)
    );
  }

  handleZip(e) {
    let value = e.target.value;
    this.setState(
      prevState => ({
        newUser: {
          ...prevState.newUser,
          zip: value
        }
      }),
      //() => console.log(this.state.newUser)
    );
  }

  handleEmail(e) {
    let value = e.target.value;
    this.setState(
      prevState => ({
        newUser: {
          ...prevState.newUser,
          email: value
        }
      }),
      //() => console.log(this.state.newUser)
    );
  }

  handlePhone(e) {
    let value = e.target.value;
    this.setState(
      prevState => ({
        newUser: {
          ...prevState.newUser,
          phone: value
        }
      }),
      //() => console.log(this.state.newUser)
    );
  }

  handleFormSubmit(e) {
    e.preventDefault();
    let userData = this.state.newUser;

    console.log(userData);

    this.registrations.push(userData);

    console.log(this.registrations);

    // fetch("http://example.com", {
    //   method: "POST",
    //   body: JSON.stringify(userData),
    //   headers: {
    //     Accept: "application/json",
    //     "Content-Type": "application/json"
    //   }
    // }).then(response => {
    //   response.json().then(data => {
    //     console.log("Successful" + data);
    //   });
    // });
  }

  handleClearForm(e) {
    e.preventDefault();
    this.setState({
      newUser: {
        firstname: "",
        lastname: "",
        npinum: "",
        address1: "",
        address2: "",
        city: "",
        state: "",
        zip: "",
        // address: {
        //     address1: "",
        //     address2: "",
        //     city: "",
        //     state: "",
        //     zip: "",
        //   },
        phone: "",
        email: ""
      }
    });
  }

  render() {
    return (
      <form className="container-fluid" onSubmit={this.handleFormSubmit}>
        {/* First Name */}
        <Text
          inputType={"text"}
          title={"First Name:"}
          name={"firstname"}
          value={this.state.newUser.firstname}
          placeholder={"Enter your first name"}
          handleChange={this.handleFirstName}
        />{" "}
        {/* Last Name */}
        <Text
          inputType={"text"}
          title={"Last Name:"}
          name={"lastname"}
          value={this.state.newUser.lastname}
          placeholder={"Enter your age"}
          handleChange={this.handleLastName}
        />{" "}
        {/* NPI Number */}
        <Text
          title={"NPI Number:"}
          name={"npinum"}
          options={this.state.npinum}
          value={this.state.newUser.npinum}
          placeholder={"Enter your NIP Number"}
          handleChange={this.handleNPInum}
        />{" "}
        {/* Address 1*/}
        <Text
          title={"Address 1:"}
          name={"address1"}
          options={this.state.address1}
          value={this.state.newUser.address1}
          placeholder={"Enter first part of address"}
          handleChange={this.handleAddress1}
        />{" "}
        {/* Address 2*/}
        <Text
          title={"Address 2:"}
          name={"address2"}
          options={this.state.address2}
          value={this.state.newUser.address2}
          placeholder={"Enter second part of address"}
          handleChange={this.handleAddress2}
        />{" "}
        {/* City */}
        <Text
          title={"Zip Code:"}
          name={"zip"}
          options={this.state.zip}
          value={this.state.newUser.zip}
          placeholder={"Enter Zip code"}
          handleChange={this.handleZip}
        />{" "}
        {/* Zip */}
        <Text
          title={"City:"}
          name={"city"}
          options={this.state.city}
          value={this.state.newUser.city}
          placeholder={"Enter city"}
          handleChange={this.handleCity}
        />{" "}
        {/* State */}
        <Text
          title={"State:"}
          name={"state"}
          options={this.state.state}
          placeholder={"Enter state"}
          handleChange={this.handleState}
        />{" "}
        {/* Phone Number */}
        <Text
          title={"Phone Number:"}
          name={"phone"}
          value={this.state.newUser.phone}
          handleChange={this.handlePhone}
          placeholder={"Enter your phone number"}
        />
        {/* Phone Number */}
        <Text
          title={"Email Address:"}
          name={"email"}
          value={this.state.newUser.email}
          handleChange={this.handleEmail}
          placeholder={"Enter your email"}
        />
        {/* Submit */}
        <Button
          action={this.handleFormSubmit}
          type={"primary"}
          title={"Submit"}
          style={buttonStyle}
        />{" "}
        {/*Clear */}
        <Button
          action={this.handleClearForm}
          type={"secondary"}
          title={"Clear"}
          style={buttonStyle}
        />{" "}
        {/* Clear the form */}
        <hr/>
      </form>
    );
  }
}

const buttonStyle = {
  margin: "10px 10px 10px 10px"
};

export default Container;
